import { Injectable, ElementRef, ViewChild, Inject } from '@angular/core';
import { DOCUMENT } from '@angular/platform-browser';

import * as FileSaver from 'file-saver';
import * as XLSX from 'xlsx';
import * as _ from "lodash";

declare var jsPDF: any;

const EXCEL_TYPE = 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8';
const EXCEL_EXTENSION = '.xlsx';

@Injectable()
export class ExcelService {

    constructor(@Inject(DOCUMENT) private document: Document) { }

    public exportAsExcelFile(json: any[], excelFileName: string, isGenric = false, gen: any): void {

        const worksheet: XLSX.WorkSheet = XLSX.utils.json_to_sheet(json, { dateNF: 'DD-MMM-YYYY' });
        var wb = XLSX.utils.book_new();
        let count = 2;
        if (isGenric) {

            _.each(gen, (item) => {
                let headCount = 1;
                _.each(item, (currentItem, index) => {
                    let property = item[index];
                    if (_.startsWith(property, 'http')) {
                        // for (let i = 1; i < 27; i++) {
                        let letter = String.fromCharCode(64 + headCount);
                        worksheet[letter + count].l = { Target: property, Tooltip: "link" };
                        // if (i == item.length) { break; }
                        // };
                    }
                    headCount += 1;
                });
                count += 1;
            });
        }

        XLSX.utils.book_append_sheet(wb, worksheet, "People");
        // const workbook: XLSX.WorkBook = { Sheets: { 'data': worksheet }, SheetNames: ['data'] };
        const excelBuffer: any = XLSX.write(wb, { bookType: 'xlsx', type: 'buffer' });
        this.saveAsExcelFile(excelBuffer, excelFileName);
    }
    /* generate a download */
    private generatedownload(s) {
        var buf = new ArrayBuffer(s.length);
        var view = new Uint8Array(buf);
        for (var i = 0; i != s.length; ++i) view[i] = s.charCodeAt(i) & 0xFF;
        return buf;
    }
    private saveAsExcelFile(buffer: any, fileName: string): void {
        const data: Blob = new Blob([this.generatedownload(buffer)], {
            type: EXCEL_TYPE
        });
        FileSaver.saveAs(data, fileName + '_export_' + new Date().getTime() + EXCEL_EXTENSION);
    }

    public downloadPdf(data: any, hearder: any, mass: any, partNumber: any, description: string) {
        var imgData = 'data:image/png;base64,';

        var total = "Total Mass " + mass + " kg";
        var doc = new jsPDF();
        var col = ["Item", "Part", "Rev", "Description", "Unit Mass", "Usage"];
        var rows = [];
        doc.setProperties({
            title: 'Bom'
        });
        var header = function (data) {
            doc.setFontSize(12);
            doc.setTextColor(40);
            doc.setFontStyle('normal');
            doc.text(total, data.settings.margin.left, 26, 0, 0, 0);
        };

        var options = {
            addPageContent: header,
            margin: {
                top: 30
            }
        };

        var count = 1;
        data.forEach(rowData => {
            var row = [];
            row.push(count);
            hearder.forEach(element => {
                row.push(rowData[element.name]);
            });
            rows.push(row);
            count++;
        });

        doc.setLineWidth(2);
        doc.line(20, 35, 60, 35);

        doc.setDrawColor(255, 0, 0);// draw red lines

        doc.setLineWidth(0.1);
        doc.line(0, 21, 500, 21);

        //  const elementToPrint = document.getElementById('bomdetail');
        doc.addImage(imgData, 'PNG', 5, 3, 15, 15);
        doc.text(40, 5, "MMD MINING MACHINERY DEVELOPMENTS LIMITED");
        doc.setFontSize(13);
        doc.text(55, 12, description);
        doc.setFontSize(10);
        doc.text(80, 18, 'Part Number : ' + partNumber);
        // doc.setFontSize(10);
        doc.text(130, 27, "Total Mass " + mass + " kg");

        doc.autoTable(col, rows, {
            margin: {
                top: 30
            }
        });
        //doc.addHTML(elementToPrint, () => {
        doc.save('bomdetail.pdf');
        //});

    }



}